import React, { useEffect, useState } from 'react';
import { useHistory, withRouter } from 'react-router-dom';
import axios from 'axios';

class Lobby extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            players: [],
            opponents: [],
            userId: null
        }
        this.getCurrentUser = this.getCurrentUser.bind(this);
        this.getUser = this.getUser.bind(this);
    }

    async getUser(id) {
        const user = await axios({
            method: 'GET',
            url: `http://localhost:8000/users/${id}/`,
            headers: {
                Authorization: `JWT ${localStorage.getItem('token')}`,

            },
        });
        return user;
    }

    async getCurrentUser() {
        const user = await axios({
            method: 'GET',
            url: `http://localhost:8000/rest-auth/user/`,
            headers: {
                Authorization: `JWT ${localStorage.getItem('token')}`,

            },
        });
        return user;
    }

    async componentDidMount() {
        // get user
        const user = await this.getCurrentUser()
        this.setState({
            userId: user.data.id
        }, ()=> console.log('userId updated'));

        let res = await axios({
            method: 'GET',
            url: 'http://localhost:8000/team/get_team/',
            headers: {
                Authorization: `JWT ${localStorage.getItem('token')}`,

            },
        });

        if (res.data.team.length > 0) {
            console.log(res);
        }
        else if (!res.data.queryroom.players_waiting.includes(this.state.userId)) {
            alert('You need to join the queue!');
            history.push('/play/')
        }
        else {
            alert('You need to join the queue!');
            history.push('/play/')
        }


    }

    render() {
        return (
            <div>
                Lobby Page

                <h1>Your Team</h1>
                {this.state.players.map((item, index) => <p key={index + item}>{item.first_name}</p>)}
            </div>
        )
    }
}

export default withRouter(Lobby);
