import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';

export default function Lobby() {

    const [players, setPlayers] = useState([]);
    const [opponents, setOpponents] = useState([]);
    const history = useHistory();

    const processTeams = (res) => {
        const team = res.data.team[0].users;
        const resOpponent = res.data.opponents[0].users;

        team.map(item => getUser(item).then((user) => setPlayers([...players, user.data])));
        resOpponent.map(item => getUser(item).then((user) => {
            console.log('opponent')
            console.log(user);
            setOpponents(opponents => [...opponents, user.data]);
            console.log(opponents);
        }));

        console.log(players);
        console.log(opponents);
    }

    const getUser = async (id) => {
        const user = await axios({
            method: 'GET',
            url: `http://localhost:8000/users/${id}/`,
            headers: {
                Authorization: `JWT ${localStorage.getItem('token')}`,

            },
        });
        // console.log('getUser');
        // console.log(user);
        return user;
    }

    useEffect(() => {
        axios({
            method: 'GET',
            url: 'http://localhost:8000/team/get_team/',
            headers: {
                Authorization: `JWT ${localStorage.getItem('token')}`,

            },
        }).then((res) => {
            if (res.data.team.length > 0) {
                processTeams(res);
            }
            else if (!res.data.queryroom.players_waiting.includes(this.state.userId)) {
                alert('You need to join the queue!');
                history.push('/play/')
            }
            else {
                alert('You need to join the queue!');
                history.push('/play/')
            }
        })
    }, [])

    return (
        <div>
            Lobby Page

            <h1>Your Team</h1>
            {players.map((item, index) => <p key={index + item}>{item.first_name}</p>)}
        </div>
    )
}
