            this.ref.current.classList.add(`style="width: 300px"`);
            this.ref.current.appendChild(child);
        }
    }

    render() {
        const { classes } = this.props;

        return (
            <div className={classes.track} ref={this.ref} />
        )
    }
}

export default withStyles(useStyles)(Track);
